INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (136982, 'Paraldehyde', 'Meperidine', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137139/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (136983, 'Paramethadione', 'Meperidine', 'Moderate', 'The central nervous system and respiratory depressant effects of meperidine may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered, particularly in elderly or debilitated patients.', NULL, 'Caution and dosage adjustments are advisable when meperidine is used in combination with other narcotic analgesics, general anesthetics, phenothiazines, sedative-hypnotics, tranquilizers, tricyclic antidepressants, or other CNS depressants such as alcohol.', NULL, 'Caution and dosage adjustments are advisable when meperidine is used in combination with other narcotic analgesics, general anesthetics, phenothiazines, sedative-hypnotics, tranquilizers, tricyclic antidepressants, or other CNS depressants such as alcohol. A lower dosage of meperidine should be considered initially, then titrated carefully according to pain level and clinical response. Meperidine dosage reductions of 25% to 50% have been recommended for patients receiving phenothiazines and other tranquilizers.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137140/', '[1] Lambertsen CJ, Wendel H, Longenhagen JB "The separate and combined respiratory effects of chlorpromazine and meperidine in normal men controlled at 46 mm Hg alveolar pCO2." J Pharmacol Exp Ther 131 (1961): 381-93[2] Hoffman JC, Smith TC "The respiratory effects of meperidine and propiomazine in man." Anesthesiology 32 (1970): 325-3