INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (137213, 'Paricalcitol', 'Methylphenobarbital', 'Moderate', 'Coadministration with CYP450 inducers such as rifampin, isoniazid, barbiturates, and certain anticonvulsants may decrease the pharmacologic effects of vitamin D analogs. These agents are thought to induce the hepatic conversion of vitamin D to inactive metabolites and have been shown to reduce circulating levels of active vitamin D, sometimes accompanied by reduced serum calcium and increased parathyroid hormone levels. Patients on long-term anticonvulsant therapy have occasionally developed osteomalacia, presumably due to interference with vitamin D and calcium metabolism. There have also been isolated reports of patients who responded poorly to vitamin D supplements during treatment with phenytoin and/or primidone.', NULL, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects.', NULL, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects. Dosage adjustments may be necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137370/', '[1] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Hectorol (doxercalciferol)." Genzyme Corporation, Cambridge, MA.[4] "Product Information. One-Alpha (alfacalcidol)." Pharmel Inc, Montreal, IN.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (137214, 'Paroxetine', 'Methylphenobarbital', 'Moderate', 'The efficacy of anticonvulsants may be diminished during coadministration with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitor (SNRIs). Antidepressants including SSRIs and SNRIs can reduce seizure threshold.Coadministration of SSRIs or SNRIs with some anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and valproic acid, may increase the risk of hyponatremia. The risk appears to be dose-related, and elderly patients and patients who are volume depleted (e.g., diuretic use) may be at greater risk.', NULL, 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications.', NULL, 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications. Treatment with SSRIs and SNRIs should be discontinued if seizures develop or seizure frequency increases. Patients receiving SSRIs or SNRIs with anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and/or valproic acid, should also have serum sodium levels measured regularly and monitored for development of hyponatremia, particularly when higher dosages of these medications are used. Signs and symptoms of hyponatremia include nausea, vomiting, headache, difficulty concentrating, memory impairment, confusion, malaise, lethargy, muscle weakness or spasms, and unsteadiness. In more severe and/or acute cases, hallucination, syncope, seizure, coma, respiratory arrest, and death may occur.', 'A