INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (137253, 'Remifentanil', 'Methylphenobarbital', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137410/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (137254, 'Ribociclib', 'Methylphenobarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ribociclib, which is a substrate of the isoenzyme.', NULL, 'The potential for diminished therapeutic effects of ribociclib should be considered when prescribed with inducers of CYP450 3A4, particularly moderate inducers including efavirenz and nevirapine.', NULL, 'The potential for diminished therapeutic effects of ribociclib should be considered when prescribed with inducers of CYP450 3A4, particularly moderate inducers including efavirenz and nevirapine. Pharmacologic effects of ribociclib should be monitored more closely whenever an inducer is added to or withdrawn from therapy. Alternative treatment may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137411/', '[1] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[5] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363024);
