INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (137356, 'Febuxostat', 'Mercaptopurine', 'Major', 'Coadministration with febuxostat may significantly increase the plasma concentrations of xanthine oxidase substrates such as azathioprine, mercaptopurine, and theophylline. Toxicity may result. The proposed mechanism is inhibition of xanthine oxidase by febuxostat. Although drug interaction studies have not been conducted with febuxostat and xanthine oxidase substrates, the interaction has been reported with allopurinol, another xanthine oxidase inhibitor. Severe bone marrow suppression and other toxicities have been associated with concomitant use of allopurinol and mercaptopurine (6-MP) or azathioprine. Increased theophylline concentrations have also been reported during coadministration with allopurinol at dosages of 600 mg/day or greater.', NULL, 'The use of febuxostat in patients treated with azathioprine, mercaptopurine, or theophylline is considered contraindicated.', NULL, 'The use of febuxostat in patients treated with azathioprine, mercaptopurine, or theophylline is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137513/', '[1] "Product Information. Uloric (febuxostat)." Takeda Pharmaceuticals America, Lincolnshire, IL.[2] Schmidt LE, Dalhoff K "Food-drug interactions." Drugs 62 (2002): 1481-502', 1767363024);
