        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1406521, 'Delavirdine', 'Ramelteon', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 and/or 2C9 may increase the plasma concentrations and pharmacologic effects of ramelteon, which is partially metabolized by these isoenzymes.', NULL, 'Caution is advised if ramelteon is prescribed in combination with potent inhibitors of CYP450 3A4 (e.', NULL, 'Caution is advised if ramelteon is prescribed in combination with potent inhibitors of CYP450 3A4 (e.g., itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, delavirdine, nefazodone, protease inhibitors, ketolide and certain macrolide antibiotics) and/or CYP450 2C9 (e.g., fluconazole, gemfibrozil, imatinib, miconazole). A reduction in the ramelteon dosage may be necessary in patients who experience excessive sedation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
