        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1406522, 'Delavirdine', 'Ranitidine (bismuth citrate)', 'Moderate', 'Theoretically, H2 antagonists and proton pump inhibitors may decrease the gastrointestinal absorption of delavirdine. The proposed mechanism is a pH-dependent reduction in drug dissolution or absorption.', NULL, 'While the clinical significance of this potential interaction is unknown, the manufacturer recommends against the chronic use of these drugs with delavirdine due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of delavirdine.', NULL, 'While the clinical significance of this potential interaction is unknown, the manufacturer recommends against the chronic use of these drugs with delavirdine due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of delavirdine.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
