    ) VALUES (1406526, 'Delavirdine', 'Retapamulin (topical)', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of retapamulin, which is primarily metabolized by the isoenzyme. However, dosage adjustments are not necessary due to the low systemic exposure to retapamulin following topical application.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
