        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1406528, 'Delavirdine', 'Rifabutin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 such as rifabutin may significantly decrease the plasma concentrations of delavirdine, which is primarily metabolized by the isoenzyme. In addition, delavirdine may increase the plasma concentrations of rifabutin by inhibiting its metabolism via CYP450 3A4.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant administration of delavirdine with rifabutin is considered contraindicated.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant administration of delavirdine with rifabutin is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
