        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1406531, 'Delavirdine', 'Rilpivirine', 'Moderate', 'The concomitant use of other nonnucleoside reverse transcriptase inhibitors (NNRTIs), such as efavirenz, etravirine, and nevirapine, may reduce the plasma concentrations of rilpivirine, which may increase the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of rilpivirine. The concomitant use of delavirdine may increase plasma concentrations of rilpivirine.', NULL, 'The manufacturer recommends against coadministration of rilpivirine with other NNRTIs.', NULL, 'The manufacturer recommends against coadministration of rilpivirine with other NNRTIs.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
