        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1406532, 'Delavirdine', 'Rimegepant', 'Moderate', 'Coadministration with strong inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of rimegepant, which is primarily metabolized by the isoenzyme.', NULL, 'Coadministration of rimegepant with strong inhibitors of CYP450 3A4 should be avoided.', NULL, 'Coadministration of rimegepant with strong inhibitors of CYP450 3A4 should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
