    ) VALUES (1406554, 'Delavirdine', 'Sodium bicarbonate', 'Moderate', 'INTERVAL: Aluminum and magnesium containing antacids have been reported to decrease the bioavailability of delavirdine by an average of 41%. The mechanism may be related to decreased absorption of delavirdine. Other antacids may also interact.', NULL, 'Delavirdine administration should be separated by at least 1 hour from one of these preparations.', NULL, 'Delavirdine administration should be separated by at least 1 hour from one of these preparations.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
