        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1406579, 'Delavirdine', 'Thioridazine', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may increase the plasma concentrations of thioridazine and its two active metabolites, mesoridazine and sulforidazine, all of which are substrates of the isoenzyme.', NULL, 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated.', NULL, 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated. Depending on the elimination half-life of these drugs, a considerable waiting period may be appropriate following their discontinuation before thioridazine is initiated. For example, the manufacturer of fluoxetine recommends that thioridazine not be administered within 5 weeks after discontinuing fluoxetine because of the drug''s long half-life.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
