        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1406612, 'Delavirdine', 'Voriconazole', 'Moderate', 'Based on in vitro data, coadministration with delavirdine may increase the plasma concentrations of voriconazole. The mechanism is delavirdine inhibition of CYP450 2C9 and 2C19, the isoenzymes responsible for the metabolic clearance of voriconazole. Conversely, voriconazole may also increase the plasma concentrations of delavirdine via inhibition of CYP450 3A4.', NULL, 'Caution is advised if voriconazole must be used concomitantly with delavirdine.', NULL, 'Caution is advised if voriconazole must be used concomitantly with delavirdine. Patients should be frequently monitored for drug toxicity during coadministration of these drugs, and the dosage(s) adjusted accordingly.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1406613, 'Delavirdine', 'Voxelotor', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the systemic exposure (AUC) to voxelotor, which has been shown to be a substrate of this isoenzyme.', NULL, 'The use of voxelotor with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'The use of voxelotor with potent CYP450 3A4 inhibitors should generally be avoided. Alternative therapeutic agents with less inhibitory potential should be considered whenever possible. If coadministration is required, the manufacturer recommends reducing the voxelotor dose to 1000 mg once daily. Patients should be closely monitored for signs of voxelotor-related adverse effects, including headache, diarrhea, abdominal pain, nausea, and vomiting.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1406614, 'Delavirdine', 'Warfarin', 'Moderate', 'Concomitant use may increase warfarin levels. The probable mechanism is inhibition of CYP450 metabolism by delavirdine. Other anticoagulants may also be affected.', NULL, 'Frequent monitoring of the INR and patient response and tolerance is strongly recommended.', NULL, 'Frequent monitoring of the INR and patient response and tolerance is strongly recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools. Dosage should be adjusted as appropriate.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1406615, 'Delavirdine', 'Zanubrutinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of zanubrutinib, which is primarily metabolized by the isoenzyme.', NULL, 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg once daily when coadministered with potent CYP450 3A4 inhibitors.', NULL, 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg once daily when coadministered with potent CYP450 3A4 inhibitors. Patients should be closely monitored for development of zanubrutinib-related toxicities, and further dosage adjustments made or treatment withheld as needed in accordance with the product labeling. Following discontinuation of the CYP450 3A4 inhibitor, the previous dosage of zanubrutinib should be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
