INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1406627, 'Denosumab', 'Denileukin diftitox', 'Moderate', 'Concomitant use of immunosuppressive or myelosuppressive agents with denosumab may increase the risk of serious infections. Denosumab binds to and inhibits the receptor activator of nuclear factor kappa-B ligand (RANKL), which is expressed on activated T and B lymphocytes and in lymph nodes. Thus, denosumab alone may increase the risk of infections.', NULL, 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents.', NULL, 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. The need for continued denosumab therapy should be assessed when serious infections occur during treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
