INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1406952, 'Desipramine', 'Cocaine (nasal)', 'Major', 'Concomitant use of cocaine with sympathomimetic agents such as epinephrine and phenylephrine may lead to additive cardiovascular effects. The proposed mechanism involves increased circulating catecholamines and increased activity of the sympathetic nervous system.', 'DDInter', 1767317476, 'Concomitant use of cocaine with sympathomimetic agents should be avoided. If coadministration is required, prolonged vital sign and ECG monitoring may be necessary.', 'Synergism', 'Concomitant use of cocaine with sympathomimetic agents should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1406953, 'Desipramine', 'Conjugated estrogens (topical)', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy. Simultaneous TCA toxicity and reduced effects have been reported. Akathisia has also been reported in some women taking this combination. The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', 'DDInter', 1767317476, 'The clinical significance of this interaction has not been established. Monitoring for altered effects may be advisable during concomitant therapy. Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'The clinical significance of this interaction has not been established.', NULL, NULL, 'Minor', 'pharmacodynamic');
