INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1407093, 'Desipramine', 'Lasmiditan', 'Major', 'Coadministration of lasmiditan and serotonergic agents may increase the risk of serotonin syndrome. Lasmiditan binds to the 5-HT(1F) receptor, where it presumably exerts therapeutic effects via agonist actions.', 'DDInter', 1767317476, 'Caution is advised during coadministration of lasmiditan and agents that increase serotonin. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Extra caution is advised when increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life.', 'Synergism', 'Caution is advised during coadministration of lasmiditan and agents that increase serotonin.', NULL, NULL, 'Major', 'pharmacodynamic');
