INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (138656, 'Dobutamine', 'Metipranolol (ophthalmic)', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of pressor agents by blocking beta-1 adrenergic receptors in the heart. Vasopressors may be less effective if the patient is receiving, or has recently received, a beta-blocking drug. In addition, peripheral vascular resistance may increase due to unopposed alpha-adrenergic effects of pressor agents in the presence of beta-blockade. Theoretically, the interaction may also occur with beta-blocker ophthalmic preparations, since they may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations. A beta-blocker such as propranolol may be used to treat cardiac arrhythmias that occur during administration of pressor agents like mephentermine, metaraminol, norepinephrine, or phenylephrine.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138814/', '[1] "Product Information. Dobutrex (dobutamine)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Intropin (dopamine)." DuPont Pharmaceuticals, Wilmington, DE.[3] Richards DA, Prichard BN, Hernandez R "Circulatory effects of noradrenaline and adrenaline before and after labetalol." Br J Clin Pharmacol 7 (1979): 371-8', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (138657, 'Doxacurium', 'Metipranolol (ophthalmic)', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138815/', '[1] Loan PB, Connolly FM, Mirakhur RK, Kumar N, Farling P "Neuromuscular effects of rocuronium in patients receiving beta-adrenoreceptor blocking, calcium entry blocking and anticonvulsant drugs." Br J Anaesth 78 (1997): 90-1[2] Harrah MD, Way WL, Katzung BG "The interaction of d-tubocurarine with antiarrhythmic drugs." Anesthesiology 33 (1970): 406-10[3] Rozen MS, Whan FM "Prolonged curarization associated with propranolol." Med J Aust 1 (1972): 467-8[4] Murthy VS, Patel KD, Elangovan RG, Hwang TF, Solochek SM, Steck JD, Laddu AR "Cardiovascular and neuromuscular effects of esmolol during induction of anesthesia." J Clin Pharmacol 26 (1986): 351-7[5] Glynne GL "Drug interaction?" Anaesthesia 39 (1984): 293[6] Herishanu Y, Rosenberg P "Beta-blockers and myasthenia gravis." Ann Intern Med 83 (1975): 834-5[7] Yate B, Mostafa SM "Drug interaction?" Anaesthesia 39 (1984): 728-9[8] Chapple DJ, Clark JS, Hughes R "Interaction between atracurium and drugs used in anaesthesia." Br J Anaesth 55 Suppl 1 (1983): s17-22', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (138658, 'Ephedrine', 'Metipranolol (ophthalmic)', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of ephedrine by blocking beta-1 adrenergic receptors in the heart. Noncardioselective beta-blockers can antagonize the bronchodilating effects of ephedrine by blocking beta-2 adrenergic receptors in smooth muscles of the bronchial tree. The interaction is less likely to occur with cardioselective beta-blockers, which generally have little effect on beta-2 adrenergic receptors at therapeutic dosages.', NULL, 'Clinicians should be alert to the potential for diminished cardiac response when parenteral ephedrine is used in patients treated with beta-blockers, including ophthalmic formulations.', NULL, 'Clinicians should be alert to the potential for diminished cardiac response when parenteral ephedrine is used in patients treated with beta-blockers, including ophthalmic formulations. Noncardioselective beta-blockers, including ophthalmic formulations, should generally be avoided in patients using ephedrine-containing preparations for bronchospastic diseases. If beta-blocker therapy is necessary, an agent with beta-1 selectivity (e.g., atenolol, metoprolol, betaxolol) is considered safer. However, caution is advised, especially with higher dosages of the beta-blocker.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138816/', '[1] "Product Information. Ephedrine Sulfate (ephedrine)." Akorn Inc, Buffalo Grove, IL.', 1767363024);
