INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (138666, 'Fenoldopam', 'Metipranolol (ophthalmic)', 'Moderate', 'The use of fenoldopam in combination with beta-blockers has not been studied. Theoretically, beta-blockers may potentiate the risk of severe, symptomatic hypotension by inhibiting the sympathetic reflex response to fenoldopam-induced blood pressure decreases.', NULL, 'Concomitant use of fenoldopam with beta-blockers should be avoided if possible.', NULL, 'Concomitant use of fenoldopam with beta-blockers should be avoided if possible. The same precaution may be applicable to beta-blocker ophthalmic preparations, as they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Moreover, since fenoldopam may cause a dose-dependent increase in intraocular pressure, caution should be exercised if it is administered to patients with glaucoma or ocular hypertension receiving ophthalmic beta-blockers.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138824/', '[1] "Product Information. Corlopam (fenoldopam)." Neurex, Menlo Park, CA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (138667, 'Fingolimod', 'Metipranolol (ophthalmic)', 'Moderate', 'The risk of bradycardia and atrioventricular (AV) block may be increased during initiation of fingolimod treatment in patients receiving beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or AV conduction such as alectinib, atazanavir, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, 'Fingolimod has not been adequately studied in patients receiving other drugs that can slow the heart rate or AV conduction.', NULL, 'Fingolimod has not been adequately studied in patients receiving other drugs that can slow the heart rate or AV conduction. Close monitoring is recommended during initiation of fingolimod treatment in these patients. The first dose should always be administered in a setting where resources to appropriately manage symptomatic bradycardia are available.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138825/', '[1] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Revised recommendations for cardiovascular monitoring and use of multiple sclerosis drug Gilenya (fingolimod). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm303192.htm#data." ([2012 May 14]):[2] "Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (138668, 'Flecainide', 'Metipranolol (ophthalmic)', 'Moderate', 'Beta-blockers and flecainide may have additive negative inotropic effects. Areas under the curve were increased for both drugs and negative inotropic effects occurred when flecainide and propranolol were given to normal subjects. A case of bradycardia, atrioventricular block and cardiac arrest has been reported after sotalol was added to flecainide.', NULL, 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration.', NULL, 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration. The same precaution should be observed when beta blocker ophthalmic solutions are used, since they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Patients should be advised to promptly report symptoms such as dizziness, slow or irregular heartbeats, syncope, or palpitations.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138826/', '[1] Lewis GP, Holtzman JL "Interaction of flecainide with digoxin and propranolol." Am J Cardiol 53 (1984): b52-7[2] Minish T, Herd A "Symptomatic bradycardia secondary to interaction between topical timolol maleate, verapamil, and flecainide: a case report." J Emerg Med 22 (2002): 247-9[3] Holtzman JL, Kvam DC, Berry DA, et al "The pharmacodynamic and pharmacokinetic interaction of flecainide acetate with propranolol: effects on cardiac function and drug clearance." Eur J Clin Pharmacol 33 (1987): 97-9[4] Myerburg RJ, Kessler KM, Cox MM, et al "Reversal of proarrhythmic effects of flecainide acetate and encainide hydrochloride by propranolol." Circulation 80 (1989): 1571-9[5] Warren R, Vohra J, Hunt D, Hamer A "Serious interactions of sotalol with amiodarone and flecainide." Med J Aust 152 (1990): 277', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (138669, 'Furosemide', 'Metipranolol (ophthalmic)', 'Moderate', 'Although they are often combined in clinical practice, diuretics and beta-blockers may increase the risk of hyperglycemia and hypertriglyceridemia in some patients, especially in patients with diabetes or latent diabetes. In addition, the risk of QT interval prolongation and arrhythmias (e.g. torsades de pointes) due to sotalol may be increased by potassium-depleting diuretics.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration. Patients should be advised to seek medical assistance if they experience dizziness, weakness, fainting, fast or irregular heartbeats, or loss of blood glucose control.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138828/', '[1] Dean S, Kendall MJ, Potter S, Thompson MH, Jackson DA "Nadolol in combination with indapamide and xipamide in resistant hypertensives." Eur J Clin Pharmacol 28 (1985): 29-33[2] Dornhorst A, Powell SH, Pensky J "Aggravation by propranolol of hyperglycaemic effect of hydrochlorothiazide in type II diabetics without alteration of insulin secretion." Lancet 1 (1985): 123-6[3] "Product Information. Lozol (indapamide)." Rhone-Poulenc Rorer, Collegeville, PA.[4] Marcy TR, Ripley TL "Aldosterone antagonists in the treatment of heart failure." Am J Health Syst Pharm 63 (2006): 49-58[5] Roux A, Le Liboux A, Delhotal B, Gaillot J, Flouvat B "Pharmacokinetics in man of acebutolol and hydrochlorothiazide as single agents and in combination." Eur J Clin Pharmacol 24 (1983): 801-6', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (138670, 'Gadobenic acid', 'Metipranolol (ophthalmic)', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral contrast media. In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat. The mechanism is unknown. Theoretically, beta-blocker ophthalmic solutions may also interact, as they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to contrast media.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to contrast media. If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine. Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm. Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138829/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767363024);
