INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (138885, 'Sirolimus', 'Metoclopramide', 'Moderate', 'Coadministration with gastrointestinal prokinetic agents such as cisapride, domperidone, and metoclopramide may increase the oral bioavailability of macrolide immunosuppressants. Because macrolide immunosuppressants are incompletely and variably absorbed following oral administration, increased gastrointestinal motility and gastric emptying can enhance delivery of the drugs to the small intestine where they are absorbed.', NULL, 'Pharmacologic response, renal function, and macrolide immunosuppressant blood levels should be monitored more closely whenever a gastrointestinal prokinetic agent is added to or discontinue from therapy, and the immunosuppressant dosage adjusted as necessary.', NULL, 'Pharmacologic response, renal function, and macrolide immunosuppressant blood levels should be monitored more closely whenever a gastrointestinal prokinetic agent is added to or discontinue from therapy, and the immunosuppressant dosage adjusted as necessary. Patients should be advised to contact their physician if they experience potential signs and symptoms of macrolide immunosuppressant toxicity such as fever, infection, diarrhea, tremor, headache, fatigue, or lethargy.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139044/', '[1] Prescott WA Jr, Callahan BL, Park JM "Tacrolimus toxicity associated with concomitant metoclopramide therapy." Pharmacotherapy 24 (2004): 532-7[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[4] Lew