INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (138987, 'Guanfacine', 'Metolazone', 'Moderate', 'Use of guanfacine in combination with antihypertensive agents may result in additive pharmacodynamic effects such as hypotension and syncope.', NULL, 'Caution is advised during coadministration of these agents.', NULL, 'Caution is advised during coadministration of these agents. Dosage adjustments may be required and hemodynamic responses should be monitored during concomitant therapy. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139146/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Tenex (guanfacine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Intuniv (guanfacine)." Shire US Inc, Florence, KY.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (138988, 'Haloperidol', 'Metolazone', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139147/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363024);
