INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139268, 'Ranolazine', 'Metoprolol', 'Moderate', 'Coadministration with ranolazine may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. Ranolazine has been shown in vitro to be an inhibitor of CYP450 2D6. However, concomitant use of ranolazine with other drugs that are metabolized by CYP450 2D6 such as tricyclic antidepressants and antipsychotics has not been studied.', NULL, 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ranolazine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139429/', '[1] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[2] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139269, 'Rapacuronium', 'Metoprolol', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139430/', '[1] Loan PB, Connolly FM, Mirakhur RK, Kumar N, Farling P "Neuromuscular effects of rocuronium in patients receiving beta-adrenoreceptor blocking, calcium entry blocking and anticonvulsant drugs." Br J Anaesth 78 (1997): 90-1[2] Harrah MD, Way WL, Katzung BG "The interaction of d-tubocurarine with antiarrhythmic drugs." Anesthesiology 33 (1970): 406-10[3] Rozen MS, Whan FM "Prolonged curarization associated with propranolol." Med J Aust 1 (1972): 467-8[4] Murthy VS, Patel KD, Elangovan RG, Hwang TF, Solochek SM, Steck JD, Laddu AR "Cardiovascular and neuromuscular effects of esmolol during induction of anesthesia." J Clin Pharmacol 26 (1986): 351-7[5] Glynne GL "Drug interaction?" Anaesthesia 39 (1984): 293[6] Herishanu Y, Rosenberg P "Bet