INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139307, 'Tocainide', 'Metoprolol', 'Moderate', 'The concomitant use of tocainide and beta-blockers may have additive effects on cardiac index, left ventricular function, and pulmonary wedge pressure. While significant interactions have not been reported in studies, cases of asystole have been reported in patients with preexisting electrophysiologic abnormalities after tocainide and metoprolol were coadministered. The mechanism is not known, but may be related to an additive effect on the sinus node or conduction system, especially in patients with sick sinus syndrome. Cases of confusion and paranoia have also been reported after coadministration of tocainide and propranolol.', NULL, 'The concomitant use of tocainide and beta blockers should probably be avoided in patients with atrioventricular conduction abnormalities or sick sinus syndrome.', NULL, 'The concomitant use of tocainide and beta blockers should probably be avoided in patients with atrioventricular conduction abnormalities or sick sinus syndrome. This combination should be used cautiously in other patients, with careful dose titration and close clinical and electrocardiographic monitoring for efficacy and safety.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139468/', '[1] Roden DM, Woosley RL "Tocainide." N Engl J Med 315 (1986): 41-5[2] Rubino M, Jackson E "Severe paranoia with concomitant tocainide and propranolol therapy." Clin Pharm 1 (1982): 177-9[3] Ikram H "Hemodynamic and electrophysiologic interactions between antiarrhythmic drugs and beta blockers, with special reference to tocainide." Am Heart J 100 (1980): 1076-80', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139308, 'Tofacitinib', 'Metoprolol', 'Moderate', 'Tofacitinib has been shown to decrease heart rate and prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta-blockers, digoxin, alpha-2 adrenoceptor agonists) may result in elevated risk of conduction disturbances and atrioventricular block.', NULL, 'According to the manufacturer, concomitant use of tofacitinib with other agents that lower heart rate and/or prolong the PR interval should be avoided when possible.', NULL, 'According to the manufacturer, concomitant use of tofacitinib with other agents that lower heart rate and/or prolong the PR interval should be avoided when possible. Caution is advised if concomitant use is necessary, particularly in patients with known conduction problems or severe cardiac disease. An ECG should be considered in these patients before initiating concomitant therapy and after titration to steady-state. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeats.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139469/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365207);
