INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139722, 'Terbinafine', 'Mexiletine', 'Moderate', 'Coadministration with terbinafine may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by terbinafine, which is expected to occur in patients who are CYP450 2D6 extensive metabolizers (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent).', NULL, 'Caution is advised if terbinafine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if terbinafine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever terbinafine is added to or withdrawn from therapy. Due to the long elimination half-life of terbinafine, especially following prolonged use, such interactions may be observed for several months after discontinuation of terbinafine therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139883/', '[1] Van der Kuy PH, Hooymans PM, Verkaaik AJ "Nortriptyline intoxication induced by terbinafine." BMJ 316 (1998): 441[2] "Product Information. Lamisil (terbinafine)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[3] AbdelRahman SM, Gotschall RR, Kauffman RE, Leeder JS, Kearns GL "Investigation of terbinafine as a CYP2D6 inhibitor in vivo." Clin Pharmacol Ther 65 (1999): 465-72[4] Gupta AK, Katz HI, Shear NH "Terbinafine and potential drug interactions - Reply." J Am Acad Dermatol 43 (2000): 883-4[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139723, 'Tizanidine', 'Mexiletine', 'Major', 'Coadministration with inhibitors of CYP450 1A2 may significantly increase the plasma concentrations and pharmacologic effects of tizanidine, which is a sensitive substrate of the isoenzyme.', NULL, 'Concomitant use of tizanidine with CYP450 1A2 inhibitors should generally be avoided.', NULL, 'Concomitant use of tizanidine with CYP450 1A2 inhibitors should generally be avoided. Otherwise, caution is advised if coadministration is required. Dosage adjustments may be necessary in patients who experience excessive adverse effects of tizanidine such as drowsiness, dizziness, lightheadedness, hypotension, and bradycardia.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139884/', '[1] Granfors MT, Backman JT, Neuvonen M, Ahonen J, Neuvonen PJ "Fluvoxamine drastically increases concentrations and effects of tizanidine: A potentially hazardous interaction." Clin Pharmacol Ther 75 (2004): 331-41[2] "Product Information. Vioxx (rofecoxib)." Merck & Co, Inc, West Point, PA.[3] "Product Information. Zelboraf (vemurafenib)." Genentech, South San Francisco, CA.[4] Granfors MT, Backman JT, Neuvonen M, Neuvonen PJ "Ciprofloxacin greatly increases concentrations and hypotensive effect of tizanidine by inhibiting its cytochrome P450 1A2-mediated presystemic metabolism." Clin Pharmacol Ther 76 (2004): 598-606[5] Momo K, Doki K, Hosono H, Homma M, Kohda Y "Drug interaction of tizanidine and fluvoxamine." Clin Pharmacol Ther 76 (2004): 509-10[6] Momo K, Homma M, Kohda Y, Ohkoshi N, Yoshizawa T, Tamaoka A "Drug interaction of tizanidine and ciprofloxacin: Case report." Clin Pharmacol Ther 80 (2006): 717-9[7] Granfors MT, Backman JT, Laitila J, Neuvonen PJ "Tizanidine is mainly metabolized by cytochrome P450 1A2 in vitro." Br J Clin Pharmacol 57 (2004): 349-53[8] "Product Information. Zanaflex (tizanidine)." Acorda Therapeutics, Hawthorne, NY.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139724, 'Tocilizumab', 'Mexiletine', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139885/', '[1] "Product Information. Skyrizi (risankizumab)." AbbVie US LLC, North Chicago, IL.[2] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Gamifant (emapalumab)." Sobi Inc, Ardmore, PA.[5] "Product Information. Kevzara (sarilumab)." sanofi-aventis, Bridgewater, NJ.[6] "Product Information. Simponi (golimumab)." Centocor Inc, Malvern, PA.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc., Horsham, PA, PA.[9] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.[10] "Product Information. Ilumya (tildrakizumab)." Merck & Company Inc, Whitehouse Station, NJ.[11] "Product Information. Actemra (tocilizumab)." Genentech, South San Francisco, CA.[12] "Product Information. Amevive (alefacept)." Biogen, Cambridge, MA.[13] "Product Information. Remicade (infliximab)." Centocor Inc, Malvern, PA.[14] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company, Indianapolis, IN.[15] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[16] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[17] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[18] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[19] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU,