INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139738, 'Cisapride', 'Miconazole', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 including azole antifungal agents may significantly increase the plasma concentrations of cisapride, which is primarily metabolized by the isoenzyme. The use of cisapride has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, the concomitant use with clotrimazole or miconazole mucous membrane preparations should be avoided if possible.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, the concomitant use with clotrimazole or miconazole mucous membrane preparations should be avoided if possible. Otherwise, patients should be cautioned about the potential for interaction and advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139899/', '[1] Bedford TA, Rowbotham DJ "Cisapride: drug interactions of clinical significance." Drug Saf 15 (1996): 167-75[2] Michalets EL, Williams CR "Drug interactions with cisapride: clinical implications." Clin Pharmacokinet 39 (2000): 49-75[3] "Product Information. ORAVIG (mi