INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139744, 'Entrectinib', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to entrectinib may increase the risk and/or severity of adverse effects such as cognitive impairment, mood disorders, dizziness, sleep disturbances, liver enzyme elevations, hyperuricemia, congestive heart failure, edema, myocarditis, QT prolongation, vision problems, anemia, and neutropenia.', NULL, 'Caution is advised when entrectinib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when entrectinib is used with CYP450 3A4 inhibitors. Pharmacologic response to entrectinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the entrectinib dosage adjusted as necessary in accordance with the product labeling. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139905/', '[1] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[4] Dimartini A "Isoniazid, tricyclics and the ''''cheese reaction''''." Int Clin Psychopharmacol 10 (1995): 197-8[5] "Product Information. INH (isoniazid)." Ciba Pharmaceuticals, Summit, NJ.[6] Smith CK, Durack DT "Isoniazid and reaction to cheese." Ann Intern Med 88 (1978): 520-1[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] Self TH, Chrisman CR, Baciewicz AM, Bronze MS "Isoniazid drug and food interactions." Am J Med Sci 317 (1999): 304-11[9] Uragoda CG, Kottegoda SR "Adverse reactions to isoniazid on ingestion of fish with a high histamine content." Tubercle 58 (1977): 83-9', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139745, 'Enzalutamide', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', NULL, 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased side effects such as seizure, asthenia, fatigue, diarrhea, arthralgia, musculoskeletal pain, paresthesia, hot flushes, peripheral edema, headache, dizziness, insomnia, hematuria, anxiety, and hypertension.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139906/', '[1] "Product Information. Xtandi (enzalutamide)." Astellas Pharma US, Inc, Deerfield, IL.', 1767363024);
