INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139804, 'Nortriptyline', 'Miconazole', 'Moderate', 'The coadministration with azole antifungal agents may increase the plasma concentrations of some tricyclic antidepressants (TCAs). Elevated TCA levels may result in CNS toxicity, anticholinergic symptoms, and QT interval prolongation. The mechanism is decreased TCA clearance due to azole-related inhibition of the CYP450 3A4 isoenzyme, which is partially responsible for the metabolism of TCAs like amitriptyline, clomipramine, imipramine, and possibly others.', NULL, 'During concomitant therapy with azole antifungal agents, patients stabilized on their TCA regimen should be observed for increased pharmacologic response to the TCA, and the dosage adjusted accordingly if necessary.', NULL, 'During concomitant therapy with azole antifungal agents, patients stabilized on their TCA regimen should be observed for increased pharmacologic response to the TCA, and the dosage adjusted accordingly if necessary. Serum TCA levels should be monitored following initiation or discontinuation of azole antifungal therapy. Patients should be advised to notify their caregiver if they experience excessive dry mouth, visual disturbances, urinary retention, dizziness, syncope, fast or irregular heartbeat, constipation, or seizures.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139965/', '[1] Dorsey ST, Biblio LA "Prolonged QT interval and torsades de pointes caused by the combination of fluconazole and amitriptyline." Am J Emerg Med 18 (2000): 227-9[2] Gannon RH "Fluconazole-nortriptyline drug interaction." Ann Pharmacother 26 (1992): 1456[3] Spina E, Avenoso A, Campo GM, Scordo MG, Caputi AP, Perucca E "Effect of ketoconazole on the pharmacokinetics of imipramine and desipramine in healthy subjects." Br J Clin Pharmacol 43 (1997): 315-8[4] Spina E, Avenoso A, Campo GM, Scordo MG, Caputi AP, Perucca E "Effect of ketoconazole on the pharmacokinetics of imipramine and desipramine in healthy subjects." Br J Clin Pharmacol 43 (1997): 315-8[5] Robinson RF, Nahata MC, Olshefski RS "Syncope associated with concurrent amitriptyline and fluconazole therapy." Ann Pharmacother 34 (2000): 1406-9[6] Newberry DL, Bass SN, Mbanefo CO "A fluconazole/amitriptyline drug interaction in three male adults." Clin Infect Dis 24 (1997): 270-1', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139805, 'Olaparib', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of olaparib, which is primarily metabolized by the isoenzyme. Increased exposure to olaparib may increase the risk of adverse effects such as hematologic toxicity, nausea, vomiting, diarrhea, anorexia, dyspepsia, and abdominal pain or discomfort.', NULL, 'Caution is advised during concomitant use of olaparib with CYP450 3A4 inhibitors.', NULL, 'Caution is advised during concomitant use of olaparib with CYP450 3A4 inhibitors. A dosage reduction may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139966/', '[1] "Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363024);
