INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139812, 'Paricalcitol', 'Miconazole', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of paricalcitol, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when paricalcitol is used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when paricalcitol is used with potent CYP450 3A4 inhibitors. Serum calcium and phosphorus levels should be monitored closely. If clinically significant hypercalcemia develops, the paricalcitol dose should be reduced or interrupted and support therapy administered.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139973/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139813, 'Pazopanib', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pazopanib, which is primarily metabolized by the isoenzyme. Although not studied, the interaction may increase the risk of QT interval prolongation and torsade de pointes arrhythmia as well as severe and fatal hepatotoxicity associated with the use of pazopanib.', NULL, 'Caution is advised if pazopanib is prescribed in combination with inhibitors of CYP450 3A4.', NULL, 'Caution is advised if pazopanib is prescribed in combination with inhibitors of CYP450 3A4. Pharmacologic response to pazopanib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the pazopanib dosage adjusted as necessary. Patients should have liver function tests (ALT, AST, bilirubin), electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, they should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139974/', '[1] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139814, 'Pexidartinib', 'Miconazole', 'Major', 'Coadministration of pexidartinib with moderate CYP450 3A4 inhibitors may significantly increase the plasma concentrations and the incidence and severity of adverse effects of pexidartinib, including potentially fatal hepatotoxicity. The proposed mechanism is inhibition of CYP450 3A4, the primary isoenzyme responsible for the metabolic clearance of pexidartinib.', NULL, 'The use of pexidartinib with a moderate CYP450 3A4 inhibitor should generally be avoided.', NULL, 'The use of pexidartinib with a moderate CYP450 3A4 inhibitor should generally be avoided. If concomitant use is required, the dose of pexidartinib should be reduced according to the manufacturer''s recommendations. If concomitant use of a moderate CYP450 3A4 inhibitor is discontinued, the dose of pexidartinib may be increased, after 3 plasma half-lives of the moderate CYP450 3A4 inhibitor, to the dose that was used prior to starting the moderate CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139975/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139815, 'Phenytoin', 'Miconazole', 'Moderate', 'Miconazole may increase plasma hydantoin concentrations. Phenytoin toxicity has been reported with concurrent parenteral and oral miconazole. The mechanism may be related to inhibition of hepatic CYP450 2C9, the major metabolic pathway for phenytoin.', NULL, 'If miconazole and hydantoins must be used concomitantly, caution is recommended, as is close observation for clinical and laboratory evidence of altered phenytoin effect.', NULL, 'If miconazole and hydantoins must be used concomitantly, caution is recommended, as is close observation for clinical and laboratory evidence of altered phenytoin effect. Patients should be advised to notify their physician if they experience symptoms of possible hydantoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139976/', '[1] Rolan PE, Somogyi AA, Drew MJ, et al "Phenytoin intoxication during treatment with parenteral miconazole." Br Med J (Clin Res Ed) 287 (1983): 1760[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[4] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral feedings administered through nasogastric tubes." JPEN J Parenter Enteral Nutr 14 (1990): 513-6[5] Krueger KA, Garnett WR, Comstock TJ, Fitzsimmons WE, Karnes HT, Pellock JM "Effect of two administration schedules of an enteral nutrient formula on phenytoin bioavailability." Epilepsia 28 (1987): 706-12[6] "Product Information. Dilantin (phenytoin)." Parke-Davis, Morris Plains, NJ.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] Doak KK, Haas CE, Dunnigan KJ, et al. "Bioavailability of phenytoin acid and phenytoin sodium with enteral feedings." Pharmacotherapy 18 (1998): 637-45[9] Rodman DP, Stevenson TL, Ray TR "Phenytoin malabsorption after jejunostomy tube delivery." Pharmacotherapy 15 (1995): 801-5[10] Sellers EM, Holloway MR "Drug kinetics and alcohol ingestion." Clin Pharmacokinet 3 (1978): 440-52[11] Guidry JR, Eastwood TF, Curry SC "Phenytoin absorption in volunteers receiving selected enteral feedings." West J Med 150 (1989): 659-61[12] Marvel ME, Bertino JS "Comparative effects of an elemental and a complex enteral feeding formulation on the absorption of phenytoin suspension." JPEN J Parenter Enteral Nutr 15 (1991): 316-8[13] Holtz L, Milton J, Sturek JK "Compatibility of medications with enteral feedings." JPEN J Parenter Enteral Nutr 11 (1987): 183-6[14] Haley CJ, Nelson J "Phenytoin-enteral feeding interaction." DICP 23 (1989): 796-8[15] Faraji B, Yu PP "Serum phenytoin levels of patients on gastrostomy tube feeding." J Neurosci Nurs 30 (1998): 55-9[16] Au Yeung SC, Ensom MH "Phenytoin and enteral feedings: does evidence support an interaction?" Ann Pharmacother 34 (2000): 896-905[17] Sandor P, Sellers EM, Dumbrell M, Khouw V "Effect of short- and long-term alcohol use on phenytoin kinetics in chronic alcoholics." Clin Pharmacol Ther 30 (1981): 390-7', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139816, 'Pimavanserin', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pimavanserin, which is primarily metabolized by the isoenzyme. High plasma levels of pimavanserin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is advised when pimavanserin is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when pimavanserin is used with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as peripheral edema and QT prolongation, and the pimavanserin dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139977/', '[1] "Product Information. Nuplazid (pimavanserin)." Accelis Pharma, East Windsor, NJ.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139817, 'Pimecrolimus (topical)', 'Miconazole', 'Minor', 'Theoretically, coadministration with CYP450 3A inhibitors may increase the plasma concentrations of pimecrolimus, which has been shown in vitro to undergo metabolism by this subfamily of hepatic microsomal enzymes. Although clinically significant drug interactions are not expected due to minimal systemic absorption of topically administered pimecrolimus, the possibility cannot be ruled out.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139978/', '[1] "Product Information. Elidel (pimecrolimus topical)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139818, 'Pimozide', 'Miconazole', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 including azole antifungal agents may significantly increase the plasma concentrations of pimozide, which is partially metabolized by the isoenzyme. The use of pimozide has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death. The interaction has not been studied with clotrimazole troches or miconazole buccal tablets. Although systemic absorption following mucous membrane exposure is limited, the potential for interaction with drugs metabolized by CYP450 3A4 such as pimozide cannot be ruled out.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, the concomitant use with clotrimazole or miconazole mucous membrane preparations should be avoided if possible.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, the concomitant use with clotrimazole or miconazole mucous membrane preparations should be avoided if possible. Otherwise, patients should be cautioned about the potential for interaction and advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139979/', '[1] "Product Information. Orap Tablets (pimozide)." Gate Pharmaceuticals, Sellersville, PA.[2] Krahenbuhl S, Sauter B, Kupferschmidt H, Krause M, Wyss PA, Meier PJ "Case report: reversible QT prolongation with torsades de pointes in a patient with pimozide intoxication." Am J Med Sci 309 (1995): 315-6[3] "Product Information. ORAVIG (miconazole)." Strativa Pharmaceuticals, a Division of Par Pharmaceuticals, Inc., Woodcliff Lake, NJ.[4] Glassman AH, Bigger JT Jr "Antipsychotic drugs: prolonged QTc interval, torsade de pointes, and sudden death." Am J Psychiatry 158 (2001): 1774-82[5] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[6] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[7] "Product Information. Orap Tablets (pimozide)." Gate Pharmaceuticals, Sellersville, PA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139819, 'Pirfenidone', 'Miconazole', 'Moderate', 'Coadministration with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 may increase the plasma concentrations of pirfenidone, especially when used in addition to a moderate or potent CYP450 1A2 inhibitor. Pirfenidone is primarily (70% to 80%) metabolized by CYP450 1A2, with minor contribution from CYP450 2C9, 2C19, 2D6, and 2E1.', NULL, 'Concomitant use of pirfenidone with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 in addition to a moderate or potent inhibitor of CYP450 1A2 (e.', NULL, 'Concomitant use of pirfenidone with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 in addition to a moderate or potent inhibitor of CYP450 1A2 (e.g., certain fluoroquinolones, oral contraceptives, deferasirox, methoxsalen, mexiletine, thiabendazole, ticlopidine, zileuton) should be avoided. No particular precaution is necessary if pirfenidone and CYP450 2C9, 2C19, 2D6, and/or 2E1 inhibitors are coadministered without a moderate or potent CYP450 1A2 inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139980/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Esbriet (pirfenidone)." Intermune Inc, Brisbane, CA.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[5] "Product Information. Esbriet (pirfenidone)." Intermune Inc, Brisbane, CA.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139820, 'Polatuzumab vedotin', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin. MMAE is a substrate for CYP450 3A4.', NULL, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be monitored for development or exacerbation of toxicities such as peripheral neuropathy, myelosuppression, opportunistic infections, tumor lysis syndrome and hepatotoxicity, and the dosing of polatuzumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139981/', '[1] "Product Information. Polivy (polatuzumab vedotin)." Genentech, South San Francisco, CA.', 1767363024);
