INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139971, 'Midazolam', 'Levacetylmethadol', 'Major', 'The central nervous system and respiratory depressant effects of levomethadyl acetate may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered.', NULL, 'Caution and dosage adjustments are advisable if CNS depressants are prescribed to patients on levomethadyl acetate maintenance.', NULL, 'Caution and dosage adjustments are advisable if CNS depressants are prescribed to patients on levomethadyl acetate maintenance. Alcohol use should be avoided, and patients should be advised to avoid driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination until they know how the medications affect them. Patients who are known to abuse sedatives, tranquilizers, antidepressants, benzodiazepines, and alcohol should be warned of the risk of serious and potentially fatal overdose if these substances are taken concurrently.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140132/', '[1] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[2] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[3] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.', 1767363024);
