INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (140079, 'Telithromycin', 'Midazolam', 'Major', 'Coadministration with telithromycin may significantly increase the plasma concentrations of midazolam and other benzodiazepines that are metabolized by CYP450 3A4 and demonstrate a high first-pass effect, such as triazolam. The mechanism is telithromycin inhibition of CYP450 3A4 metabolism in the liver and especially the intestine.', NULL, 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with telithromycin should be avoided if possible.', NULL, 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with telithromycin should be avoided if possible. Benzodiazepines that are not CYP450 3A4 substrates such as lorazepam, nitrazepam, and temazepam may be appropriate alternatives during therapy with telithromycin. Depending on organism susceptibility, azithromycin and dirithromycin may be appropriate alternatives during therapy with midazolam or triazolam. Intravenous midazolam may be administered with telithromycin with close monitoring of vital signs and respiratory status, and the dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140240/', '[1] "Product Information. Ketek (telithromycin)." Aventis Pharmaceuticals, Bridgewater, NJ.[2] European Agency for the Evaluation of Medicinal Products. Committee for Proprietary Medicinal Products "European P