INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (140130, 'Deserpidine', 'Midodrine', 'Moderate', 'Postganglionic adrenergic blocking agents may potentiate the pharmacologic effects of direct-acting sympathomimetic amines, while the hypotensive effect of the former may be diminished. Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings, thus they may sensitize adrenergic receptors to direct-acting sympathomimetics.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension. Conversely, alternatives to postganglionic adrenergic blocking agents should be considered in patients treated with sympathomimetic amines. If the combination is used, blood pressure and heart rate should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140291/', '[1] Hardman JG, Gilman AG, Limbird LE eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 9th ed." New York, NY: McGraw-Hill (1995):[2] Muelheims GH, Entrup RW, Paiewonsky D, Mierzwiak DS "Increased sensitivity of the heart to catecholamine-induced arrhythmias following guanethidine." Clin Pharmacol Ther 6 (1965): 757-62[3] Spiers AS, Calne DB "Action of dopamine on the human iris." Br Med J 4 (1969): 333-5', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (140131, 'Digitoxin', 'Midodrine', 'Moderate', 'Midodrine when administered with any agent that may slow the heart rate (i.e., cardiac glycosides, beta blockers, tricyclic antidepressants) may enhance or precipitate bradycardia, AV block or arrhythmia. The mechanism may be related to vagal reflexive slowing of the heart after midodrine administration.', NULL, 'If midodrine and cardiac glycosides are administered concomitantly, patients should be monitored for cardiac abnormalities.', NULL, 'If midodrine and cardiac glycosides are administered concomitantly, patients should be monitored for cardiac abnormalities. Patients should be advised to promptly notify their physician if they experience a slow pulse, dizziness, syncope, or cardiac awareness.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140292/', '[1] "Product Information. ProAmatine (midodrine)." Roberts Pharmaceutical Corporation, Eatontown, NJ.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (140132, 'Entecavir', 'Midodrine', 'Moderate', 'Coadministration of entecavir with another drug that is also eliminated by active tubular secretion may result in increased plasma concentrations of one or both drugs due to competitive inhibition of transporters in the renal tubules. Drugs (and/or their metabolites) that are thought to undergo active tubular secretion include acyclovir, allopurinol, aminosalicylic acid, cidofovir, cimetidine, creatine, dyphylline, famciclovir, famotidine, flecainide, ganciclovir, levetiracetam, metformin, methotrexate, midodrine, mycophenolic acid, oseltamivir, pralatrexate, probenecid, procainamide, quinidine, ranitidine, tenofovir, triamterene, trimethoprim, valacyclovir, valganciclovir, zalcitabine, zidovudine, and many of the beta-lactam and quinolone antibiotics.', NULL, 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', NULL, 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140293/', '[1] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (140133, 'Ephedrine', 'Midodrine', 'Moderate', 'The pressor effects of midodrine may be potentiated by the concomitant use of other agents that cause vasoconstriction, including sympathomimetics and ergot alkaloids. Midodrine is an alpha-adrenergic agonist and may cause marked elevation of supine arterial blood pressure, or supine hypertension.', NULL, 'Caution is advised if midodrine must be used in combination with other agents that can increase blood pressure.', NULL, 'Caution is advised if midodrine must be used in combination with other agents that can increase blood pressure. Supine and sitting blood pressures should be closely monitored. Supine hypertension can often be controlled by keeping the patient from being fully supine, for example, sleeping with the head of the bed elevated. Taking the last daily dose of midodrine 3 to 4 hours before bedtime may also help to minimize nighttime supine hypertension. Patients should be advised to contact their physician immediately if they experience symptoms of supine hypertension such as cardiac awareness, pounding in the ears, headache, and blurred vision. Midodrine should be discontinued if supine hypertension persists.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140294/', '[1] "Product Information. ProAmatine (midodrine)." Roberts Pharmaceutical Corporation, Eatontown, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365208);
