INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (140239, 'Eribulin', 'Midostaurin', 'Moderate', 'Eribulin may cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if eribulin is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if eribulin is used in combination with other drugs that can prolong the QT interval. Baseline and periodic monitoring of electrocardiograms and serum electrolytes (potassium, magnesium) should be considered, and hypokalemia or hypomagnesemia corrected prior to initiating treatment with eribulin. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140400/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Halaven (eribulin)." Eisai Inc, Teaneck, NJ.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (140240, 'Erlotinib', 'Midostaurin', 'Moderate', 'Coadministration with midostaurin may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or organic anion transporting polypeptide (OATP) 1B1 transporters. The proposed mechanism, based on in vitro data, is decreased clearance due to midostaurin-mediated inhibition of P-gp, BCRP, and/or OATP1B1 transport proteins.', NULL, 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever midostaurin is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140401/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365208);
