INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (140390, 'Revefenacin', 'Midostaurin', 'Moderate', 'Coadministration of revefenacin with inhibitors of organic anion transporting polypeptide (OATP) 1B1 and/or 1B3 may increase systemic exposure to its active metabolite, which has been reported to be a substrate of the hepatic uptake transporters.', NULL, 'Concomitant use of revefenacin with OATP 1B1 and/or 1B3 inhibitors is not recommended.', NULL, 'Concomitant use of revefenacin with OATP 1B1 and/or 1B3 inhibitors is not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140551/', '[1] "Product Information. Yupelri (revefenacin)." Mylan Specialty, Morgantown, WV.[2] "Product Information. Nexletol (bempedoic acid)." Esperion Therapeutics, Ann Arbor, MI.[3] "Product Information. Nexlizet (bempedoic acid-ezetimibe)." Esperion Therapeutics, Ann Arbor, MI.[4] "Product Information. Yupelri (revefenacin)." Mylan Specialty, Morgantown, WV.[5] "Product Information. Nexletol (bempedoic acid)." Esperion Therapeutics, Ann Arbor, MI.[6] "Product Information. Nexlizet (bempedoic acid-ezetimibe)." Esperion Therapeutics, Ann Arbor, MI.[7] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (140391, 'Ribociclib', 'Midostaurin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of midostaurin and its active metabolites, which are all substrates of the isoenzyme.', NULL, 'Caution is advised when midostaurin is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when midostaurin is used with CYP450 3A4 inhibitors. Patients should be closely monitored for increased adverse reactions (e.g., nausea, vomiting, diarrhea, edema, hyperglycemia, hyperuricemia, QT prolongation, neutropenia, lymphopenia, thrombocytopenia, anemia), especially during the first week of consecutive midostaurin administration in patients with advanced systemic mastocytosis and during the first week of midostaurin administration in each cycle of chemotherapy in patients with acute myeloid leukemia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140552/', '[1] Dutreix C, Munarini F, Lorenzo S, Roesel J, Wang Y "Investigation into CYP3A4-mediated drug-drug interactions on midostaurin in healthy volunteers." Cancer Chemother Pharmacol 72 (2013): 1223-34[2] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767365208);
