INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (140557, 'Fondaparinux', 'Mifepristone', 'Major', 'Concomitant use of anticoagulants may exacerbate mifepristone-induced vaginal bleeding in women. Prolonged, potentially heavy bleeding lasting approximately 9 to 30 days typically follows mifepristone use in termination of pregnancy. Because it is a progesterone receptor antagonist, mifepristone can also promote unopposed endometrial proliferation that may result in endometrium thickening, cystic dilatation of endometrial glands, and vaginal bleeding when used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome.', NULL, 'Concurrent anticoagulant therapy is considered a contraindication in the use of mifepristone for pregnancy termination.', NULL, 'Concurrent anticoagulant therapy is considered a contraindication in the use of mifepristone for pregnancy termination. When used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, mifepristone treatment should be administered cautiously in women who are receiving anticoagulant therapy. Patients who experience vaginal bleeding should be referred to a gynecologist for further evaluation.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140718/', '[1] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[2] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[3] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[4] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.', 1767363024);
