INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (141190, 'Ozanimod', 'Mirtazapine', 'Moderate', 'Coadministration of adrenergic agents with drugs that possess monoamine oxidase inhibition (MAOI) activity may precipitate severe hypertensive reactions and hyperpyrexia. Coadministration of serotonergic agents with drugs that possess monoamine oxidase inhibition (MAOI) activity may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', NULL, 'Until more information is available, concomitant use of ozanimod with agents that possess adrenergic and serotonergic effects should be avoided when possible.', NULL, 'Until more information is available, concomitant use of ozanimod with agents that possess adrenergic and serotonergic effects should be avoided when possible. Blood pressure and other vitals should be monitored if coadministration is required.', 'Synergism', 'Viloxazine, Esketamine', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141351/', '[1] Spiker DG, Pugh DD "Combining tricyclic and monoamine oxidase inhibitor antidepressants." Arch Gen Psychiatry 33 (1976): 828-30[2] Phillips SD, Ringo P "Phenelzine and venlafaxine interaction." Am J Psychiatry 152 (1995): 1400-1[3] "Product Information. Flexeril (cyclobenzaprine)." Merck & Co, Inc, West Point, PA.[4] "Product Information. Emsam (selegiline)." Bristol-Myers Squibb, Princeton, NJ.[5] Wigen CL, Goe