INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (141192, 'Panobinostat', 'Mirtazapine', 'Major', 'Panobinostat can cause dose-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Concomitant use of panobinostat with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Concomitant use of panobinostat with other drugs that can prolong the QT interval should generally be avoided. Anti-emetic drugs with known QT prolonging risk such as dolasetron, granisetron, ondansetron, palonosetron, and tropisetron may be used with frequent ECG monitoring. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting panobinostat therapy and periodically during treatment as clinically indicated.', 'Synergism', 'Phenelzine, Tranylcypromine, Vilazodone, Desvenlafaxine, Isocarboxazid, Oxitriptan, Tryptophan, Milnacipran, Levomilnacipran, Bupropion, Esketamine', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Niraparib, Selinexor, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141353/', '[1] "Product Information. Farydak (panobinostat)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] "Product Information. Farydak (panobinostat)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (141193, 'Papaverine', 'Mirtazapine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', NULL, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', NULL, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', 'Duloxetine, Vilazodone, Paroxetine, Desvenlafaxine, St. John''s Wort, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, More', 'Fesoterodine, Pentosan polysulfate, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Avanafil, Phenyl salicylate, Ammonium chloride, Dimethyl sulfoxide, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141354/', '[1] Vrolix M, Piessens J, De Geest H "Torsades de pointes after intracoronary papaverine." Eur Heart J 12 (1991): 273-6[2] Jain A, Jenkins MG "Intracoronary electrocardiogram