INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (141406, 'Rilonacept', 'Mitomycin', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established. Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.', 'Synergism', 'Valrubicin', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, Apremilast, Inebilizumab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141567/', '[1] "Product Information. Kineret (anakinra)." Amgen, Thousand Oaks, CA.[2] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[3] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (141407, 'Roflumilast', 'Mitomycin', 'Moderate', 'The concomitant use of roflumilast with immunosuppressive agents has not been adequately studied. Specifically, roflumilast and its active metabolite, roflumilast N-oxide, suppress the release of inflammatory mediators including leukotriene B4, reactive oxygen species, tumor necrosis factor alpha, interferon gamma, and granzyme B. Roflumilast also reduces sputum neutrophils and attenuates influx of neutrophils and eosinophils into the airways.', NULL, 'According to the product labeling for roflumilast in some countries , concomitant use with immunosuppressive agents such as methotrexate, azathioprine, infliximab, etanercept, and systemic corticosteroids (except when used short-term, e.', NULL, 'According to the product labeling for roflumilast in some countries , concomitant use with immunosuppressive agents such as methotrexate, azathioprine, infliximab, etanercept, and systemic corticosteroids (except when used short-term, e.g., in the treatment of COPD exacerbations) should be avoided if possible. Due to a lack of clinical experience, treatment with roflumilast should not be initiated, or existing treatment should be stopped, in patients receiving immunosuppressive agents and in patients with severe acute infectious diseases, cancers (except basal cell carcinoma), or severe immunological diseases (e.g., HIV infection, multiple sclerosis, lupus erythematosus, progressive multifocal leukoencephalopathy).', 'Synergism', 'Valrubicin', 'Theophylline, Omalizumab, Tezepelumab, Aminophylline, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Oxtriphylline, Mepolizumab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141568/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Daxas (roflumilast)." Nycomed Inc, Princeton, NJ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Daxas (roflumilast)." Nycomed Inc, Princeton, NJ.[5] "Product Information. Daliresp (roflumilast)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (141408, 'Rubella virus vaccine', 'Mitomycin', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'Valrubicin', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141569/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur, Swiftwater, PA.[3] "Product Information. Attenuvax (measles virus vaccine, live, attenuated)." Merck & Co, Inc, West Point, PA.[4] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014): 161[5] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Company Inc, West Point, PA.[6] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011): 1495-7[7] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[8] "Product Information. Meruvax II (rubella virus vaccine, live)." Merck & Co, Inc, West Point, PA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
