INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1408324, 'Dextroamphetamine', 'Reserpine', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, may decrease the hypotensive effect of postganglionic adrenergic blocking agents such as guanadrel, guanethidine and rauwolfia alkaloids, which work by depleting catecholamine stores from adrenergic nerve endings. CNS stimulants can have peripheral sympathetic activity, thus they may elevate blood pressure on their own as well as antagonize some of the antiadrenergic effect produced by these hypotensive agents.', 'DDInter', 1767317476, 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants. If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1408325, 'Dextroamphetamine', 'Ritodrine', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, can potentiate the adrenergic response to vasopressors and other sympathomimetic agents. Additive increases in blood pressure and heart rate may occur due to enhanced peripheral sympathetic activity.', 'DDInter', 1767317476, 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents. Pulse and blood pressure should be closely monitored.', 'Synergism', 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1408326, 'Dextroamphetamine', 'Safinamide', 'Major', 'Coadministration of nonselective monoamine oxidase (MAO) inhibitors and sympathomimetic amines has been associated with severe hypertensive reactions. The mechanism involves a synergistic sympathomimetic effect due to enhanced norepinephrine storage in adrenergic neurons (MAOI activity) and increased liberation or decreased reuptake of catecholamines (sympathomimetic activity). Because safinamide is a selective MAO-B inhibitor, hypertensive reactions would not ordinarily be expected when used with sympathomimetic amines.', 'DDInter', 1767317476, 'Coadministration of safinamide and sympathomimetic amines, such as methylphenidate, amphetamine, and their derivatives, is considered contraindicated.', 'Synergism', 'Coadministration of safinamide and sympathomimetic amines, such as methylphenidate, amphetamine, and their derivatives, is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1408327, 'Dextroamphetamine', 'Selegiline', 'Major', 'Centrally-acting sympathomimetic agents (i.e., CNS stimulants), particularly the amphetamines and amphetamine derivatives, may precipitate severe hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs). MAOIs also slow amphetamine metabolism, which may potentiate amphetamine effect on the release of norepinephrine and other monoamines from adrenergic nerve endings.', 'DDInter', 1767317476, 'In general, CNS stimulants should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine).', 'Synergism', 'In general, CNS stimulants should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1408328, 'Dextroamphetamine', 'Sibutramine', 'Major', 'Sibutramine is a potent inhibitor of norepinephrine and serotonin reuptake, and a less potent inhibitor of dopamine reuptake. As such, it may potentiate the pharmacologic response to sympathomimetic agents due to additive or synergistic effects. Although the interaction has not been studied with sibutramine, it has been reported with the selective serotonin reuptake inhibitor, fluoxetine.', 'DDInter', 1767317476, 'The use of sibutramine in combination with amphetamines and other centrally-acting appetite suppressants is considered contraindicated.', 'Synergism', 'The use of sibutramine in combination with amphetamines and other centrally-acting appetite suppressants is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
