INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1408387, 'Dexlansoprazole', 'Neratinib', 'Major', 'Coadministration with proton pump inhibitors or H2-receptor antagonists may significantly decrease the oral bioavailability of neratinib. The solubility of neratinib is pH-dependent and increases as neratinib becomes protonated at acidic pH, thus an increase in pH may interfere with its absorption.', 'DDInter', 1767317476, 'Concomitant use of neratinib with proton pump inhibitors and H2-receptor antagonists should generally be avoided. In addition, short-acting antacids could be considered if acid-suppression therapy is required, with neratinib dosing separated by at least three hours after antacid administration. If coadministered with a gastric acid reducing agent, increasing the dose of neratinib is unlikely to compensate for the loss of bioavailability.', 'Absorption', 'Concomitant use of neratinib with proton pump inhibitors and H2-receptor antagonists should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
