INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1408396, 'Dexlansoprazole', 'Pexidartinib', 'Major', 'Coadministration with proton pump inhibitors (PPIs) may significantly decrease the bioavailability and therapeutic effects of pexidartinib. The proposed mechanism is a pH-dependent reduction in drug dissolution and/or absorption. The effect of coadministration with an H2-receptor antagonist or locally-acting antacids on pexidartinib pharmacokinetics is unknown.', 'DDInter', 1767317476, 'Concomitant use of pexidartinib with proton pump inhibitors should be avoided. If acid-suppression therapy is required, pexidartinib may be administered 2 hours before or 2 hours after taking a locally-acting antacid, or if using an H2-receptor antagonist, administer pexidartinib at least 2 hours before or 10 hours after taking the H2-receptor antagonist.', 'Absorption', 'Concomitant use of pexidartinib with proton pump inhibitors should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
