INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (141837, 'Zafirlukast', 'Nateglinide', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of nateglinide, which is primarily metabolized by the isoenzyme.', NULL, 'Because the antidiabetic effect of nateglinide is dose- and concentration-dependent, close monitoring for the development of hypoglycemia is recommended during coadministration with CYP450 2C9 inhibitors.', NULL, 'Because the antidiabetic effect of nateglinide is dose- and concentration-dependent, close monitoring for the development of hypoglycemia is recommended during coadministration with CYP450 2C9 inhibitors. Patients should regularly monitor their blood sugar and learn how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitation. The dosage of nateglinide may require adjustment if an interaction is suspected. Likewise, patients should be observed for potential loss of glycemic control following discontinuation of the CYP450 2C9 inhibitor, and the nateglinide dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141998/', '[1] "Product Information. Starlix (nateglinide)" Novartis Pharmaceuticals, East Hanover, NJ.[2] Sabia H, Sunkara G, Ligueros-Saylan M, et al. "Effect of a selective CYP2C9 inhibitor on the pharmacokinetics of nateglinide in healthy subjects." Eur J Clin Pharmacol (2004):[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[4] "Product Information. Diabinese (chlorprop