INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (141859, 'Fludeoxyglucose (18F)', 'Lactitol', 'Moderate', 'Certain medications may interfere with the results of diagnostic studies using fludeoxyglucose F18 by increasing or decreasing uptake of the agent into tissues.', NULL, 'Clinicians should be aware of possible diagnostic interference associated with the following medications: colony-stimulating factors may increase uptake in spleen and bone marrow; glucose-induced hyperglycemia may interfere with uptake; dopamine and insulin may decrease myocardial and tumor cell uptake; laxative-induced intestinal muscle activity and/or irritation may increase intestinal uptake.', NULL, 'Clinicians should be aware of possible diagnostic interference associated with the following medications: colony-stimulating factors may increase uptake in spleen and bone marrow; glucose-induced hyperglycemia may interfere with uptake; dopamine and insulin may decrease myocardial and tumor cell uptake; laxative-induced intestinal muscle activity and/or irritation may increase intestinal uptake.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142020/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Metamucil (psyllium)." Procter and Gamble Pharmaceutic, Cincinnati, OH.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (141860, 'Fludrocortisone', 'Lactitol', 'Moderate', 'The overuse or abuse of laxatives can cause significant loss of electrolytes and potentiate the risk of hypokalemia associated with corticosteroid therapy. Corticosteroids promote the retention of sodium and water and the excretion of potassium. Although these effects are primarily associated with mineralocorticoids like fludrocortisone, they may also occur with higher dosages of glucocorticoids or adrenocorticotropic agents, particularly if given systemically for longer than brief periods.', NULL, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', NULL, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant therapy with corticosteroids, particularly if fludrocortisone or large doses of a glucocorticoid or adrenocorticotropic agent is given, patients should be counseled to recognize potential signs and symptoms of hypokalemia such as fatigue, myalgia, and muscle weakness. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142021/', '[1] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8[2] Blumenthal M, Goldberg A, Brinckmann J, eds. "Herbal Medicine: Expanded Commission E Monographs." Newton, MA: Integrative Medicine Communications (2000):[3] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[4] Seale JP, Compton MR "Side-effects of corticosteroid agents." Med J Aust 144 (1986): 139-42[5] Swartz SL, Dluhy RG "Corticosteroids: clinical pharmacology and therapeutic use." Drugs 16 (1978): 238-55[6] Lieberman P, Patterson R, Kunske R "Complications of long-term steroid therapy for asthma." J Allergy Clin Immunol 49 (1972): 329-36[7] Ramsahoye BH, Davies SV, el-Gaylani N, Sandeman D, Scanlon MF "The mineralocorticoid effects of high dose hydrocortisone." BMJ 310 (1995): 656-7', 1767365208);
