INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (141960, 'Calcium carbonate', 'Lactulose', 'Minor', 'Preliminary data from studies in humans and rats suggest that nonabsorbable antacids given concurrently with lactulose may inhibit the reduction in colonic pH induced by lactulose. Theoretically, this may reduce the effectiveness of lactulose in the treatment of portal-systemic encephalopathy, which requires an acidic environment in the colon to promote extraction of ammonia from the blood as well as retention/elimination of ammonia as the ammonium ion. However, the clinical relevance of this interaction has not been fully elucidated.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142121/', '[1] "Product Information. Cephulac (lactulose)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] "Product Information. Chronulac (lactulose)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767365208);
