INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142098, 'Cyproheptadine', 'Milnacipran', 'Moderate', 'Serotonin antagonists such as cyproheptadine and methysergide may diminish the pharmacologic effects of selective serotonin reuptake inhibitors (SSRIs) and other highly serotonergic agents (e.g., 5-hydroxytryptophan; buspirone; mirtazapine; nefazodone; sibutramine; St. John''s wort; trazodone; SNRIs such as duloxetine, desvenlafaxine, milnacipran, and venlafaxine). This pharmacodynamic interaction is sometimes exploited in the use of cyproheptadine and methysergide for the treatment of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Theoretically, serotonergic agents may also antagonize the pharmacologic effects of cyproheptadine and methysergide, although this effect has not been reported.', NULL, 'Patients should be monitored for potentially diminished therapeutic response to serotonergic agents during coadministration with cyproheptadine or methysergide, and dosages adjusted as necessary.', NULL, 'Patients should be monitored for potentially diminished therapeutic response to serotonergic agents during coadministration with cyproheptadine or methysergide, and dosages adjusted as necessary.', 'Antagonism', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Promethazine, More', 'Bupropion, Viloxazine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142259/', '[1] Sternbach H "The serotonin syndrome." Am J Psychiatry 148 (1991): 705-13[2] Christensen RC "Adver