INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142409, 'Butabarbital', 'Nebivolol', 'Moderate', 'Coadministration with barbiturates may decrease the plasma concentrations and pharmacologic effects of certain beta-blockers when administered orally. The proposed mechanism is barbiturate induction of hepatic microsomal and first-pass metabolism.', NULL, 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days.', NULL, 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days. Pharmacologic response to beta-blockers should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the beta-blocker dosage adjusted as necessary. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'Metabolism', 'Suvorexant, Nitrazepam, Thiopental, Eszopiclone, Ramelteon, Lemborexant, Methohexital, Estazolam, Zopiclone, Melatonin, Valerian', 'Acetylsalicylic acid, Sacubitril, Aliskiren, Carteolol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142571/', '[1] Mantyla R, Mannisto P, Nykanen S, Koponen A, Lamminsivu U "Pharmacokinetic interactions of timolol with vasodilating drugs, food and phenobarbitone in healthy human volunteers." Eur J Clin Pharmacol 24 (1983): 227-30[2] Seideman P, Borg K, Haglund K, Von Bahr C, "Decreased plasma concentrations and clinical effects of alprenolol during combined treatment with pentobarbitone in hypertension." Br J Clin Pharmacol 23 (1987): 267-71[3] Haglund K, Seideman P, Colliste P, Borg KO, von Bahr C "Influence of pentobarbital on metoprolol plasma levels." Clin Pharmacol Ther 26 (1979): 326-9[4] Sotaniemi EA, Anttila M, Pelkonen RO, Jarvensivu P, Sundquist H "Plasma clearance of propranolol and sotalol and hepatic drug-metabolizing enzyme activity." Clin Pharmacol Ther 26 (1979): 153-61[5] Branch RA, Herman RJ "Enzyme induction and beta-adrenergic receptor blocking drugs." Br J Clin Pharmacol 17 (1984): s77-84[6] Collste P, Seideman P, Borg K, Haglund K, Von Bahr C "Influence of pentobarbital on effect and plasma levels of alprenolol and 4-hydroxy-alprenolol." Clin Pharmacol Ther 25 (1979): 423-7', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142410, 'Chlorpromazine', 'Nebivolol', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Droperidol, Pimozide, Pimavanserin', 'Sacubitril, Aliskiren', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142572/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information