    ) VALUES (1409299, 'Dorzolamide (ophthalmic)', 'Diclofenamide', 'Moderate', 'Following ophthalmic administration, brinzolamide and dorzolamide are systemically absorbed and may have additive pharmacologic effects with other, systemically administered carbonic anhydrase inhibitors. The risk of adverse effects such as drowsiness, paresthesia, tinnitus, electrolyte imbalance, metabolic acidosis, and gastrointestinal disturbances may be increased.', NULL, 'The concomitant use of ocular and systemic carbonic anhydrase inhibitors is not recommended.', NULL, 'The concomitant use of ocular and systemic carbonic anhydrase inhibitors is not recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
