INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142504, 'Astemizole', 'Nefazodone', 'Major', 'Nefazodone has been shown in vitro to inhibit CYP450 3A4, the isoenzyme responsible for the metabolism of astemizole. Accumulation of astemizole following administration of other drugs that inhibit CYP450 3A4 has resulted in prolonged QT interval and, rarely, serious arrhythmias and death.', NULL, 'Although there have been no clinical reports of adverse events related to the coadministration of nefazodone and astemizole, the manufacturer of nefazodone considers this combination to be contraindicated.', NULL, 'Although there have been no clinical reports of adverse events related to the coadministration of nefazodone and astemizole, the manufacturer of nefazodone considers this combination to be contraindicated.', 'Metabolism', 'Methdilazine, Ketotifen, Meclizine, Chlorcyclizine, Thiethylperazine, Dexchlorpheniramine, Phenindamine, Azelastine, Pheniramine', 'Phenelzine, Duloxetine, Tranylcypromine, Vilazodone, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142667/', '[1] "Product Information. Serzone (nefazodone)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Hismanal (astemizole)." Janssen Pharmaceutica, Titusville, NJ.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of The