INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142548, 'Erlotinib', 'Nefazodone', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of erlotinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if erlotinib must be used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised if erlotinib must be used with potent CYP450 3A4 inhibitors. A dosage reduction or temporary interruption of therapy should be considered in patients who experience undue adverse effects of erlotinib such as severe diarrhea (i.e., that which is unresponsive to loperamide or results in dehydration), severe skin reactions, or severe liver function test abnormalities.', 'Metabolism', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Maprotiline, Fluoxetine, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Isocarboxazid, Viloxazine, Amoxapine, Imipramine, Esketamine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142711/', '[1] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450 enzymes." Clin Cancer Res 13 (2007): 3731-7[2] "Product Information. Tarceva (erlotinib)." Genentech, South San Francisco, CA.[3] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450 enzymes." Clin Cancer Res 13 (2007): 3731-7[4] "Product Information. Tarceva (erlotinib)." Genentech, South San Francisco, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142549, 'Escitalopram', 'Nefazodone', 'Major', 'Concomitant use of agents with serotonergic activity such as serotonin reuptake inhibitors, monoamine oxidase inhibitors, tricyclic antidepressants, 5-HT1 receptor agonists, ergot alkaloids, cyclobenzaprine, lithium, St. John''s wort, phenylpiperidine opioids, dextromethorphan, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucination, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', NULL, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', NULL, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is advised when increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate 