INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142566, 'Etravirine', 'Nefazodone', 'Moderate', 'Coadministration of etravirine with a drug that is both a substrate as well as inhibitor of CYP450 3A4 may result in increased plasma concentrations of etravirine and decreased plasma concentrations of the other drug. Etravirine itself is a substrate and inducer of CYP450 3A4. Theoretically, etravirine may induce metabolism of the coadministered drug while its own metabolism may be inhibited by the coadministered drug.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with a drug that is an inhibitor of CYP450 3A4.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with a drug that is an inhibitor of CYP450 3A4. In addition, dosage adjustments may be required for the coadministered drug if it is also a substrate of CYP450 3A4.', 'Metabolism', 'Sertraline, Maprotiline, Milnacipran, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, Amoxapine, Imipramine, Esketamine, More', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Cobicistat, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142729/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.', 1767369485);
