INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142675, 'Palbociclib', 'Nefazodone', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of palbociclib, which is a substrate of the isoenzyme. The risk of adverse effects such as infections, neutropenia, leukopenia, anemia, thrombocytopenia, anorexia, nausea, vomiting, diarrhea, stomatitis, alopecia, asthenia, peripheral neuropathy, and epistaxis may be increased.', NULL, 'Concomitant use of palbociclib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of palbociclib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, consider reducing the palbociclib dosage to 75 mg once daily. Following discontinuation of the potent CYP450 3A4 inhibitor, the palbociclib dosage should be returned (after 3 to 5 half-lives of the inhibitor) to that used prior to initiation of the inhibitor.', 'Metabolism', 'Sertraline, Maprotiline, Fluoxetine, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Amoxapine, Imipramine, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142838/', '[1] "Product Information. Ibrance (palbociclib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2