INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142707, 'Posaconazole', 'Nefazodone', 'Moderate', 'Coadministration with posaconazole may significantly increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by posaconazole.', NULL, 'Caution is advised if posaconazole must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if posaconazole must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever posaconazole is added to or withdrawn from therapy.', 'Metabolism', 'St. John''s Wort, Vortioxetine, Milnacipran, Isocarboxazid, Viloxazine, Imipramine, Esketamine, Fluvoxamine, Phenelzine, Tranylcypromine, Trimipramine, More', 'Caspofungin, Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142870/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Sonata (zaleplon)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Sansone-Parsons A, Krishna G, Calzetta A, et al. "Effect of a nutritional supplement on posaconazole pharmacokinetics following oral administration to healthy volunteers." Antimicrob Agents Chemother 50 (2006): 1881-3[4] Walravens J, Brouwers J, Spriet I, Tack J, Annaert P, Augustijns P "Effect of pH and Comedication on Gastrointestinal Absorption of Posaconazole: Monitoring of Intraluminal and Plasma Drug Concentrations." Clin Pharmacokinet 50 (2011): 725-34[5] "Product Information. Noxafil (posaconazole)." Schering-Plough Corporation, Kenilworth, NJ.[6] Krishna G, Moton A, Ma L, Malavade D, Medlock M, McLeod J "Effect of gastric pH, dosing regimen and prandial state, food and meal timing relative to dose, and gastro-intestinal motility on absorption and pharmacokinetics of the antifungal posaconazole." 18th European Congress of Clinical Microbiology and Infectious Diseases April (2008): 20', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142708, 'Pralsetinib', 'Nefazodone', 'Major', 'Coadministration with potent CYP450 3A inhibitors may significantly increase the plasma concentrations of pralsetinib, which is primarily metabolized by the isoenzyme. Increased exposure to pralsetinib may increase the risk of serious adverse effects such as interstitial lung disease/pneumonitis, liver transaminase elevations, hypertension, and hemorrhage.', NULL, 'Concomitant use of pralsetinib with potent CYP450 3A inhibitors should generally be avoided.', NULL, 'Concomitant use of pralsetinib with potent CYP450 3A inhibitors should generally be avoided.', 'Metabolism', 'Phenelzine, Duloxetine, Tranylcypromine, Desvenlafaxine, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Bupropion, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142871/', '[1] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation, Cambridge, MA.[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation, Cambridge, MA.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142709, 'Pravastatin', 'Nefazodone', 'Moderate', 'An isolated case report suggests a possible interaction between nefazodone and pravastatin resulting in presumed musculoskeletal toxicity manifested as asymptomatic elevations in creatine kinase level. The mechanism of interaction is unknown, as nefazodone has not been shown to significantly affect pravastatin metabolism or plasma levels.', NULL, 'All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever.', NULL, 'All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Others', 'Gemfibrozil, Fenofibric acid, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Clofibrate, Colestipol, Bempedoic acid, More', 'Phenelzine, Desipramine, Tranylcypromine, Vilazodone, Maprotiline, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Milnacipran, Vortioxetine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142872/', '[1] Bottorf MB "Possible interaction between nefazodone and pravastatin." Ann Pharmacother 34 (2000): 538[2] Alderman CP "Possible interaction between nefazodone and pravastatin." Ann Pharmacother 34 (2000): 538-9[3] Alderman CP "Possible interaction between nefazodone and pravastatin." Ann